from youtube_transcript_api import YouTubeTranscriptApi

GROVER_videoID = "PYhvoxLw8N4"
ROHAN_videoID = "HfKXabrKUQ8"

transcriptList = YouTubeTranscriptApi.list_transcripts(GROVER_videoID)
