translatedTranscript = None
for transcript in transcriptList:
  if transcript.language_code == 'hi':
    # print(transcript)
    translatedTranscript = transcript.translate('en').fetch()
    # print(translatedTranscript)

if translatedTranscript:
    with open('transcript.txt', 'a') as file:
        for entry in translatedTranscript:
            file.write(entry['text'] + " ")
else:
    print("Hindi transcript not found or could not be translated.")